module.exports = {
  run: function() {
    console.log('run');

    var repl = require("repl");

    var replServer = repl.start({
      prompt: "node via stdin> ",
      input: process.stdin,
      output: process.stdout
    });

    replServer.context.doThis = function() {
      console.log('do this');
    }
  }
};
